import React, { useState } from 'react'
// import './Header.css'
import styled from 'styled-components'
import BurgerButton from '../Home/BurgerButton/BurgerButton';
import Svg from './Svg/Svg';


const Header = () => {

    const [clicked, setClicked] = useState(false);
    const handleClick = () => {
        setClicked(!clicked);
    }
    return (

        <header className='header'>
            <Navbar>
                <div>
                    <a href="/">
                        <img className='logo' src="/Assets/logo.png" alt="logo" />
                    </a>
                </div>
                <ul className={`links ${clicked ? 'active' : ''}`}>
                    <a href='/'>Home</a>
                    <a href='/'>About</a>
                    <a href='/'>Contact</a>
                </ul>
                <div className='signContainer'>
                    <a href="#sign up">Sign up</a>
                    <p>/</p>
                    <a href="#signIn">Sign in</a>
                </div>
                <div className='burguer'>
                    <BurgerButton
                        clicked={clicked}
                        handleClick={handleClick} />
                </div>
            </Navbar>
            <div className='svg'>
                <Svg />
            </div>
            <BgDiv className={`initial ${clicked ? 'active' : ''}`}></BgDiv>
        </header>
    );
}

export default Header;

const Navbar = styled.nav`
        height: 100%;
        display: flex;
        align-items: center;
        justify-content: space-between;
        margin: 0;
        border: none;
        /* background-color: rgba(243, 180.1, 90, 0.89); */
        background-color:  rgba(235, 166, 55, 0.733);
    .logo{
        width: 50px;
        padding: 10px;
        margin-left: 10px;
        transition: width .5s;
        :hover{
            width: 60px;
        }
    }

    .signContainer{
        display: flex;
        margin: 0;
        a{
            font-weight: bold;
            color: black;
            text-decoration: none;
            padding-right: 0.5rem;
            padding-left: 0.5rem;
        }
    }

    .links {
        a{
            margin-right: 15px;
            text-decoration: none;
            font-weight: bold;
            font-size: 20px;
            color: inherit;
            border-radius: 12px;
            transition: .6s ease; 

           :hover{
                background-color: black ;
                color: aliceblue;
                padding: 5px;
           }
        }

        @media(max-width: 768px){
                position: absolute;
                top: -1000px;
                right: -1000px;
                margin-left: auto;
                margin-right: auto;
                text-align: center;
                transition: .8s ease;
        }
    }
    
    .links.active{
        width: 100%;
        display: block;
        position: absolute;
        margin-left: auto;
        margin-right: auto;
        top: 15%;
        left: 15%;
        right: 0;
        text-align: center;
        z-index: 1;
        a{
            color: white;
            display: block;
            margin-bottom: 1rem;
            
        }
    }

   .burguer{
    @media(min-width: 768px){
        display: none;
    }
    z-index: 1;
   }
`
const BgDiv = styled.div`
position: absolute;
background-color:  rgba(157, 111, 36, 0.942);
right: -1000px;
top: -1000px;
transition: all .6s ease;
width: 100%;
height: 70%;
&.active{
    border-radius: 0 0 0 100%;
    top: 0;
    right: 0;
}
`
